mapper.select:
select con.id, u.name as owner, con.source, cus.name as customerId, con.fullname, con.appellation, con.email, con.mphone, con.job, con.birth, con.createBy, con.createTime, con.editBy, con.editTime, con.description, con.contactSummary, con.nextContactTime, con.address from tbl_contacts con left join tbl_user u on con.owner=u.id left join tbl_customer cus on cus.id=con.customerId
cus.name like '%' #{customerName} '%'
and u.name like '%' #{ownerName} '%'
and con.source=#{source}
and con.fullname like '%' #{fullname} '%'
order by con.createTime desc limit #{skipCount},#{pageSize}
